/********************************************************************
* FUNCTION y_test_tesDone_send
* 
* Send a y_test_tesDone notification
* Called by your code when notification event occurs
* 
********************************************************************/
void
    y_test_tesDone_send (
        const xmlChar *tesStatus)
{
    agt_not_msg_t *notif;
    val_value_t *parmval;
    status_t res;

    res = NO_ERR;

    if (LOGDEBUG) {
        log_debug("\nGenerating <tesDone> notification");
    }
    
    notif = agt_not_new_notification(tesDone_obj);
    if (notif == NULL) {
        log_error("\nError: malloc failed, cannot send <tesDone> notification");
        return;
    }
    
    /* add tesStatus to payload */
    parmval = agt_make_leaf(
        tesDone_obj,
        y_test_N_tesStatus,
        tesStatus,
        &res);
    if (parmval == NULL) {
        log_error(
            "\nError: make leaf failed (%s), cannot send <tesDone> notification",
            get_error_string(res));
    } else {
        agt_not_add_to_payload(notif, parmval);
    }
    
    agt_not_queue_notification(notif);
    
} /* y_test_tesDone_send */


/********************************************************************
* FUNCTION y_test_init
* 
* initialize the test server instrumentation library
* 
* INPUTS:
*    modname == requested module name
*    revision == requested version (NULL for any)
* 
* RETURNS:
*     error status
********************************************************************/
status_t
    y_test_init (
        const xmlChar *modname,
        const xmlChar *revision)
{
    agt_profile_t *agt_profile;
    status_t res;

    y_test_init_static_vars();

    /* change if custom handling done */
    if (xml_strcmp(modname, y_test_M_test)) {
        return ERR_NCX_UNKNOWN_MODULE;
    }

    if (revision && xml_strcmp(revision, y_test_R_test)) {
        return ERR_NCX_WRONG_VERSION;
    }

    agt_profile = agt_get_profile();

    res = ncxmod_load_module(
        y_test_M_test,
        y_test_R_test,
        &agt_profile->agt_savedevQ,
        &test_mod);
    if (res != NO_ERR) {
        return res;
    }
    
    test_obj = ncx_find_object(
        test_mod,
        y_test_N_test);
    if (test_mod == NULL) {
        return SET_ERROR(ERR_NCX_DEF_NOT_FOUND);
    }
    
    make_tes_obj = ncx_find_object(
        test_mod,
        y_test_N_make_tes);
    if (test_mod == NULL) {
        return SET_ERROR(ERR_NCX_DEF_NOT_FOUND);
    }
    
    cancel_tes_obj = ncx_find_object(
        test_mod,
        y_test_N_cancel_tes);
    if (test_mod == NULL) {
        return SET_ERROR(ERR_NCX_DEF_NOT_FOUND);
    }
    
    tesDone_obj = ncx_find_object(
        test_mod,
        y_test_N_tesDone);
    if (test_mod == NULL) {
        return SET_ERROR(ERR_NCX_DEF_NOT_FOUND);
    }
    
    res = agt_rpc_register_method(
        y_test_M_test,
        y_test_N_make_tes,
        AGT_RPC_PH_VALIDATE,
        y_test_make_tes_validate);
    if (res != NO_ERR) {
        return res;
    }
    
    res = agt_rpc_register_method(
        y_test_M_test,
        y_test_N_make_tes,
        AGT_RPC_PH_INVOKE,
        y_test_make_tes_invoke);
    if (res != NO_ERR) {
        return res;
    }
    
    res = agt_rpc_register_method(
        y_test_M_test,
        y_test_N_cancel_tes,
        AGT_RPC_PH_VALIDATE,
        y_test_cancel_tes_validate);
    if (res != NO_ERR) {
        return res;
    }
    
    res = agt_rpc_register_method(
        y_test_M_test,
        y_test_N_cancel_tes,
        AGT_RPC_PH_INVOKE,
        y_test_cancel_tes_invoke);
    if (res != NO_ERR) {
        return res;
    }
    
    res = agt_cb_register_callback(
        y_test_M_test,
        (const xmlChar *)"/test",
        (const xmlChar *)"2009-11-20",
        y_test_test_edit);
    if (res != NO_ERR) {
        return res;
    }
    
    /* put your module initialization code here */
    
    return res;
} /* y_test_init */


